import '../../style/App.scss';


function Collapse({title, text, largeur}){
    return <div style={{width:largeur}}>
        <button type="button" class="collapsible" onClick={open()} >{title}</button>
        <div class="content">
        <p>{text}</p>
        </div>
    </div>
}

function open (){
    document.querySelector(".collapsible").style.backgroundColor = "red";
}

export default Collapse;
