import '../../style/App.scss';


function Collapse({title, text, largeur, id}){
    return <div style={{width:largeur}}>
        <button type="button" class="collapsible" id={id} onClick={open(id)} >{title}</button>
        <div class="openCollapse">{text}</div>
        <div class="content">
        <p>{text}</p>
        </div>
    </div>
}

function open (id){

    let c = document.getElementById(id)
    if (c.style.display=="none")
        c.style.display = "flex";
    else
        c.style.display="none";
}

export default Collapse;
